package com.techgirl.SpringRedis.service;import com.techgirl.SpringRedis.model.Student;import com.techgirl.SpringRedis.repository.StudentRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class StudentService {    @Autowired    private StudentRepository studentRepository;    @Autowired    public StudentService(StudentRepository studentRepository) {        this.studentRepository = studentRepository;    }    public void addStudent(Student student) {        studentRepository.save(student);    }    public Optional<Student> getStudentById(Long student_id) {        return studentRepository.findById(student_id);    }}